import 'package:shared_preferences/shared_preferences.dart';class MyPreferences {  static late final SharedPreferences _preferences;  static void init() async {    _preferences = await SharedPreferences.getInstance();  }  static void setRegister(bool value) {    _preferences.setBool("isRegistered", value);  }  static bool? isRegister() {    return _preferences.getBool("isRegistered");  }  static void setEmail(String email) {    _preferences.setString('email', email);  }  static String? getEmail() {    return _preferences.getString('email');  }  static void setUsername(String username) {    _preferences.setString('username', username);  }  static String? getUserName() {    return _preferences.getString('username');  }  static void setFullName(String fullName) {    _preferences.setString('fullName', fullName);  }  static String? getFullName() {    return _preferences.getString('fullName');  }  static void setAvatar(String img) {    _preferences.setString('img', img);  }  static String? getAvatar() {    return _preferences.getString('img');  }  static void setTheme(bool value) {    _preferences.setBool('theme', value);  }  static bool? getTheme() {    return _preferences.getBool('theme');  }}