import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/models/book_data.dart';import '../../../../domain/repository/app_repository.dart';import '../../../../domain/repository/impl/app_repository_impl.dart';part 'playing_event.dart';part 'playing_state.dart';class PlayingBloc extends Bloc<PlayingEvent, PlayingState> {  final AppRepository _repository = AppRepositoryImpl();  PlayingBloc() : super(PlayingInitial()) {    on<GetBookByIdEvent>((event, emit) async {      try {        BookData book = await _repository.getBookById(event.id);        emit(GetBookByIdState(book));      } catch (e) {        print(e.toString());      }    });    on<OpenBookFileEvent>((event, emit) {      emit(OpenBookFileState(event.url));    });  }}